import React from "react";
import Cookies from 'universal-cookie';
const cookies = new Cookies();
class Juego extends React.Component {
    constructor(props) {
      super(props);
      this.state = {valor1: '',valor2: '',};
  
      this.handleChange = this.handleChange.bind(this);
      this.handleSubmit = this.handleSubmit.bind(this);
    }
  
    handleChange(event) {
      console.log( event.target.value);
      this.setState({valor1: event.target.value});
    }
    
    handleChange2(event) {
      console.log( event.target.value);
      this.setState({valor2: event.target.value});
    }
    handleSubmit(event) {
      
      console.log(this.state.valor1);
      event.preventDefault();
    }
  
    render() { 
        return (
            <div className="col-sm">
                    <div className="row">
                      <h1>{cookies.get('jugador1')}</h1>
                        <form onSubmit={this.handleSubmit}>
                            <label>
                            Pick your favorite flavor:
                            <select value={this.state.value} onChange={this.handleChange}>
                                <option value="1">Piedra</option>
                                <option value="2">Papel</option>
                                <option value="3">Tijera</option>
                                <option value="4">cordel</option>
                                <option value="5">perro</option>
                            </select>
                            </label>
                            <input type="submit" value="Submit" />
                        </form>
                        <hr></hr>
                        
                      <h1>{cookies.get('jugador2')}</h1>
                        <form onSubmit={this.handleSubmit}>
                            <label>
                            Pick your favorite flavor:
                            <select value={this.state.value} onChange={this.handleChange2}>
                                <option value="1">Piedra</option>
                                <option value="2">Papel</option>
                                <option value="3">Tijera</option>
                                <option value="4">cordel</option>
                                <option value="5">perro</option>
                            </select>
                            </label>
                            <input type="submit" value="Submit" />
                        </form>
                    </div>
            </div>
         );
    }
}
 
export default Juego;
