import React from "react";
import {
  BrowserRouter as Router,
  Switch,
  Route
} from "react-router-dom";
import Juego from "./Juego";
import Cookies from 'universal-cookie';
import sha256 from 'crypto-js/sha256';
// This site has 3 pages, all of which are rendered
// dynamically in the browser (not server rendered).
//
// Although the page does not ever refresh, notice how
// React Router keeps the URL up to date as you navigate
// through the site. This preserves the browser history,
// making sure things like the back button and bookmarks
// work properly.

// You can think of these components as "pages"
// in your app.
const cookies = new Cookies();
class Routes extends React.Component {
  constructor(props) {
    super(props);
    this.state = {jugador1: '',jugador2: ''};
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }
  
  handleSubmit(event) {
    let created = new Date();
    let md5Hash = sha256(created);
    event.preventDefault();
    
    this.state.count = parseInt(this.state.count)+1;
    /*const book = {
      bookID,
      bookTitle,
      bookAuthor,
    };*/
    axios
    .get('http://localhost:3001/api/stats/player/crear/')
    .catch(err => {
      console.error(err);
    });
    cookies.set('jugador1',this.state.jugador1,{path:"/"});
    cookies.set('jugador2',this.state.jugador2,{path:"/"});
    window.location.href = "/juego";
  }
  render() { 
    return (
      <Router>
          <Switch>
              <Route path="/juego">
                <Juego />
              </Route>
              <Route path="/">
              <form onSubmit={this.handleSubmit}>
                    <div className="col-sm">
                        <div className="row">
                            <div>
                                <h1>Ingresa el nombre los jugadores</h1>
                            </div>
                        </div>
                        <div className="row">
                            
                            <div className="form-group">  
                                <label>Jugador 1</label>
                                <input
                                    name="jugador1"
                                    type="text"
                                    value={ this.state.jugador1 || "" }
                                    onChange={this.handleChange} 
                                    className="form-control"/>
                            </div>
                        </div>
                        <div className="row">
                            <div className="form-group">  
                                <label>Jugador 2</label>
                                <input
                                    type="text"
                                    name="jugador2"
                                    value={ this.state.jugador2 || "" }
                                    onChange={this.handleChange} 
                                    className="form-control" />
                            </div>
                        </div>
                        <input
                            type="submit"
                            value="Enviar"
                            className="btn btn-dark"/>
                    </div>
                </form>
              </Route>
          </Switch>
      </Router>  );
  }
  
  handleChange(event) {  
    this.setState({ 
    [event.target.name] : event.target.value 
    }) 
  }
}
export default Routes;
