import React from "react";
import Cookies from 'universal-cookie';
const cookies = new Cookies();
class Juego extends React.Component {
    constructor(props) {
      super(props);
      this.state = {var1:'',var2:'',player1:true};
      this.handleChange = this.handleChange.bind(this);
      this.handleChange2 = this.handleChange.bind(this);
      this.handleSubmit = this.handleSubmit.bind(this);
    }
  
    handleChange = async event => {
      console.log('bbb');
      const target = event.target;
      const value = target.value;
      this.setState({
        'var1': value
      });
    }
    
    change = async event => {
      const target = event.target;
      const value = target.value;
      this.setState({
        'var2': value
      });
    }
    handleSubmit(event) {
      console.log(this.state.var2);
      event.preventDefault();
    }
  
    render() {
      if (this.state.player1) {
        
        const muestra = <div className="jugador1">
              <h1>{cookies.get('jugador1')}</h1>
                    <label>
                    Pick your favorite flavor:
                    <select value={this.state.value} onChange={this.handleChange}>
                        <option value="1">Piedra</option>
                        <option value="2">Papel</option>
                        <option value="3">Tijera</option>
                        <option value="4">cordel</option>
                        <option value="5">perro</option>
                    </select>
                    </label>
            </div>
      }else{
        
        muestra = <div className="jugador2">
        <h1>{cookies.get('jugador2')}</h1>
              <label>
              Pick your favorite flavor:
              <select value={this.state.value} onChange={this.change}>
                  <option value="1">Piedra</option>
                  <option value="2">Papel</option>
                  <option value="3">Tijera</option>
                  <option value="4">cordel</option>
                  <option value="5">perro</option>
              </select>
              </label>
              <input type="submit" value="Submit" />
      </div>
      }
        return (
            <div className="col-sm">
                    <div className="row">
                      <form onSubmit={this.handleSubmit}>
                        {muestra}
                      </form>
                    </div>
            </div>
         );
    }
}
 
export default Juego;
