import React from "react";
import Cookies from 'universal-cookie';
const cookies = new Cookies();
class Juego extends React.Component {
    constructor(props) {
      super(props);
      this.state = {var1:'',var2:'',num:1,nom:cookies.get('jugador1'),valorUser1:0,valorUser1:0};
      this.handleChange = this.handleChange.bind(this);
      this.handleChange2 = this.handleChange.bind(this);
      this.handleSubmit = this.handleSubmit.bind(this);
    }
  
    handleChange = async event => {
      const target = event.target;
      const value = target.value;
      console.log(value);
      this.setState({
        'var1': value
      });
    }
    
    handleSubmit= async event =>{
      const numclick = 0;
      const valor       = this.state.num;
      if (this.state.num > 1) {
          valor = this.state.num+1;
      }
      let nomMostrar  = '';
      let valorUser1;
      let valorUser2  ;
      event.preventDefault();
      if (valor % 2 == 0){
        console.log('es par');
        /*this.setState({
          'valorUser1': this.state.var1
        });
        nomMostrar = cookies.get('jugador2');*/
      }else{
        console.log('es inpar');
        /*this.setState({
          'valorUser2': this.state.var1
        });
        nomMostrar = cookies.get('jugador1');*/
      }
      if (this.state.valorUser1 == 1 && this.state.valorUser2 == 2) {
        console.log('gano la ale');
        
      }
      this.setState({
        'num': valor,
        'nom': nomMostrar
      });
    }
  
    render() {
        return (
            <div className="col-sm">
                    <div className="row">
                      <form onSubmit={this.handleSubmit}>
                      <div className="jugador1">
                        <h1>{this.state.nom}</h1>
                              <label>
                              Pick your favorite flavor:
                              <select value={this.state.value} onChange={this.handleChange}>
                                  <option value="1">Selecciona Opcion</option>
                                  <option value="1">Piedra</option>
                                  <option value="2">Papel</option>
                                  <option value="3">Tijera</option>
                                  <option value="4">cordel</option>
                                  <option value="5">perro</option>
                              </select>
                              </label>
                      </div>
                      <input type="submit" value="Submit" />
                      </form>
                    </div>
            </div>
         );
    }
}
 
export default Juego;
