import React from "react";
import Cookies from 'universal-cookie';
const cookies = new Cookies();
class Juego extends React.Component {
    constructor(props) {
      super(props);
      this.state = {var1:'',num:0,nom:cookies.get('jugador1'),valorUser1:''};
      this.handleChange = this.handleChange.bind(this);
      this.handleChange2 = this.handleChange.bind(this);
      this.handleSubmit = this.handleSubmit.bind(this);
    }
  
    handleChange = async event => {
      const target = event.target;
      const value = target.value;
      //console.log(value);
      this.setState({
        'var1': value
      });
    }
    
    handleSubmit= async event =>{
      const valor       = this.state.num + 1;
      let nomMostrar  = '';
      let valorUser1;
      let valorUser2 ;
      event.preventDefault();
      console.log('aaaaa');
      this.setState({
        'valorUser1': 2
      });
      if (valor % 2 != 0){
        valorUser2 = this.state.var1;
        nomMostrar = cookies.get('jugador2');
        console.log(this.state);
      }else{
        console.log('bb');
        this.setState({
          'valorUser2': this.state.var1
        });
        nomMostrar = cookies.get('jugador1');
      }
      //console.log(this.state.valorUser1  + ' ' + this.state.valorUser2);
      if (this.state.valorUser1 == 1 && this.state.valorUser2 == 2) {
        console.log('gano la ale');
      }
      this.setState({
        'num': valor,
        'nom': nomMostrar
      });
    }
  
    render() {
        return (
            <div className="col-sm">
                    <div className="row">
                      <form onSubmit={this.handleSubmit}>
                      <div className="jugador1">
                        <h1>{this.state.nom}</h1>
                              <label>
                              Pick your favorite flavor:
                              <select onChange={this.handleChange}>
                                  <option value="1">Selecciona Opcion</option>
                                  <option value="1">Piedra</option>
                                  <option value="2">Papel</option>
                                  <option value="3">Tijera</option>
                                  <option value="4">cordel</option>
                                  <option value="5">perro</option>
                              </select>
                              </label>
                      </div>
                      <input type="submit" value="Submit" />
                      </form>
                    </div>
            </div>
         );
    }
}
 
export default Juego;
