import React from "react";
import Cookies from 'universal-cookie';
const cookies = new Cookies();
class Juego extends React.Component {
    constructor(props) {
      super(props);
      this.state = {var1:'',num:0,nom:cookies.get('jugador1'), player1:'', player2:'',count:1};
      this.handleChange = this.handleChange.bind(this);
      this.handleChange2 = this.handleChange.bind(this);
      this.handleSubmit = this.handleSubmit.bind(this);
    }
  
    handleChange = async event => {
      const target = event.target;
      const value = target.value;
      //console.log(value);
      this.setState({
        'var1': value
      });
    }
    
    handleSubmit= async event =>{
      const valor       = parseInt(this.state.var1) + 1;
      let nomMostrar  = '';
      event.preventDefault();
      console.log(valor);
      if (valor % 2 != 0){
        this.setState({player1 : 'jugador1'});
        nomMostrar = cookies.get('jugador2');
      }else{
        this.setState({player2 : 'jugador2'});
        nomMostrar = cookies.get('jugador1');
      }
      console.log(this.state);
      /*if (this.state.valorUser1 == 1 && this.state.valorUser2 == 2) {
        console.log('gano la ale');
      }*/
      this.setState({
        'num': valor,
        'nom': nomMostrar
      });
      this.state.count = this.state.count
    }
  
    render() {
        return (
            <div className="col-sm">
                    <div className="row">
                      <form onSubmit={this.handleSubmit}>
                      <div className="jugador1">
                        <h1>{this.state.nom}</h1>
                              <label>
                              Pick your favorite flavor:
                              <select onChange={this.handleChange}>
                                  <option value="1">Selecciona Opcion</option>
                                  <option value="1">Piedra</option>
                                  <option value="2">Papel</option>
                                  <option value="3">Tijera</option>
                                  <option value="4">cordel</option>
                                  <option value="5">perro</option>
                              </select>
                              </label>
                      </div>
                      <input type="submit" value="Submit" />
                      </form>
                    </div>
            </div>
         );
    }
}
 
export default Juego;
