import React from "react";
import Cookies from 'universal-cookie';
const cookies = new Cookies();
class Juego extends React.Component {
    constructor(props) {
      super(props);
      this.state={show1:false};
      this.handleChange = this.handleChange.bind(this);
      this.handleChange2 = this.handleChange.bind(this);
      this.handleSubmit = this.handleSubmit.bind(this);
    }
  
    handleChange = async event => {
      const target = event.target;
      const value = target.value;
      //console.log(value);
      this.setState({
        'var1': value
      });
    }
    
    handleSubmit= event =>{
      let nomMostrar  = '';
      event.preventDefault();
      /*if(this.state.count == 0){
        this.setState({player1 : this.state.var1});
      }
      if (this.state.count % 2 == 0){
        this.setState({player2 :  this.state.var1});
        nomMostrar = cookies.get('jugador2');
      }else{
        this.setState({player1 : this.state.var1});
        nomMostrar = cookies.get('jugador1');
      }
      console.log(this.state);
      if (this.state.player1 == 1 && this.state.player2 == 2) {
        console.log('gano la ale');
      }
      this.setState({
        'num': this.state.count,
        'nom': nomMostrar
      });
      this.state.count = parseInt(this.state.count)+1;*/
      if(this.state.show1 == true){  
        this.setState({
          'show': false
        });
      }else{
        this.setState({
          'show': true
        });
      }
      console.log(this.state);
    }
  
    render() {
        return (
            <div className="col-sm">
                    <div className="row">
                      <form onSubmit={this.handleSubmit}>
                      <div className="jugador1">
                        <h1>{this.state.nom}</h1>
                              <label>
                              Pick your favorite flavor:
                              {
                                this.state.show?
                                  <select name="jugador1" onChange={this.handleChange}>
                                  <option value="1">Selecciona Opcion</option>
                                  <option value="1">Piedra</option>
                                  <option value="2">Papel</option>
                                  <option value="3">Tijera</option>
                                  <option value="4">cordel</option>
                                  <option value="5">perro</option>
                                  </select>
                                :
                                <div>falso</div>  
                              }
                              }
                              </label>
                      </div>
                      <input type="submit" value="Submit" />
                      </form>
                    </div>
            </div>
         );
    }
}
 
export default Juego;
