import React from "react";
import Cookies from 'universal-cookie';
const cookies = new Cookies();
class Juego extends React.Component {
    constructor(props) {
      super(props);
      this.state={show:false,count:0,selectValue1:0,selectValue2:0};
      this.handleChange = this.handleChange.bind(this);
      this.handleChange2 = this.handleChange.bind(this);
      this.handleSubmit = this.handleSubmit.bind(this);
    }
  
    handleChange = async event => {
      const target = event.target;
      const value = target.value;
      this.setState({
        'selectValue1': value
      });
    }
    selectCombo2 = async event => {
      const target = event.target;
      const value = target.value;
      console.log(value);
      this.setState({
        'selectValue2': value
      });
    }
    handleSubmit= event =>{
      let nomMostrar  = '';
      event.preventDefault();
      this.state.count = parseInt(this.state.count)+1;
      if(this.state.count == 6){
        console.log('termino');
      }else{
          if (this.state.count % 2 == 0){
            nomMostrar = cookies.get('jugador2');
          }else{
            nomMostrar = cookies.get('jugador1');
          }
          console.log(this.state.selectValue1 + ' ' + this.state.selectValue2);
          if(this.state.show == true){  
              this.setState({
                  'show': false
                });
          }else{
              this.setState({
                'show': true
              });
          }
      }
    }
  
    render() {
        return (
            <div className="col-sm">
                    <div className="row">
                      <form onSubmit={this.handleSubmit}>
                      <div className="jugador1">
                        <h1>{this.state.nom}</h1>
                              <label>
                              Pick your favorite flavor:
                              {
                                this.state.show?
                                  <select name="jugador2" value={this.state.selectValue2} onChange={this.selectCombo2}>
                                  <option value="0">Selecciona Opcion</option>
                                  <option value="1">Piedra</option>
                                  <option value="2">Papel</option>
                                  <option value="3">Tijera</option>
                                  <option value="4">cordel</option>
                                  <option value="5">perro</option>
                                  </select>
                                :
                                <select name="jugador1"  value={this.state.valor} onChange={this.handleChange}>
                                <option value="0">Selecciona Opcion</option>
                                <option value="1">Piedra</option>
                                <option value="2">Papel</option>
                                <option value="3">Tijera</option>
                                <option value="4">cordel</option>
                                <option value="5">perro</option>
                                </select> 
                              }
                              </label>
                      </div>
                      <input type="submit" value="Submit" />
                      </form>
                    </div>
            </div>
         );
    }
}
 
export default Juego;
